1.3350 148.4270 145.9030 142.7280 138.7190]';WcEcol(:,2) = [153.8120 153.5110 151.5180 148.7480 146.2590 143.0560 138.9870]';WcEcol(:,3) = [153.8120 153.5110 151.7010 149.0690 146.6150 143.3840 139.2550]';WcEcol(:,4) = [153.8120 153.5110 151.8840 149.3900 146.9710 143.7120 139.5230]';WcEcol(:,5) = [153.8120 153.5110 152.0670 149.7110 147.3270 144.0400 139.7910]';WcEcol(:,6) = [153.8120 153.5110 152.2500 150.0320 147.6830 144.3680 140.0590]';WcEcol(:,7) = [153.8120 153.5110 152.4330 150.3530 148.0390 144.6960 140.3270]';WcEcol(:,8) = [153.8120 153.5110 152.6160 150.6740 148.3950 145.0240 140.5950]';effErw1 = [0.7508 0.7373 0.7238 0.7103 0.6968 0.6833 0.6698 0.6563 0.6428 0.6293 0.6190 0.6055 0.5939 0.5842 0.5763;           0.7886 0.7765 0.7644 0.7523 0.7402 0.7281 0.7160 0.7039 0.6918 0.6797 0.6701 0.6581 0.6477 0.6389 0.6316;           0.8264 0.8157 0.8050 0.7943 0.7836 0.7729 0.7622 0.7515 0.7408 0.7301 0.7212 0.7107 0.7015 0.6936 0.6869;           0.8642 0.8549 0.8456 0.8363 0.8270 0.8177 0.8084 0.7991 0.7898 0.7805 0.7723 0.7633 0.7553 0.7483 0.7422];effErw2 = [0.5684 0.5608 0.5544 0.5483 0.5429 0.5377 0.5332 0.5292 0.5275 0.5259 0.5240 0.5222 0.5191;           0.6244 0.6175 0.6116 0.6060 0.6010 0.5962 0.5920 0.5882 0.5862 0.5843 0.5822 0.5802 0.5767;           0.6804 0.6742 0.6688 0.6637 0.6591 0.6547 0.6508 0.6472 0.6449 0.6427 0.6404 0.6382 0.6343;           0.7364 0.7309 0.7260 0.7214 0.7172 0.7132 0.7096 0.7062 0.7036 0.7011 0.6986 0.6962 0.6919];effErw = [effErw1 effErw2];effEcol = [0.9020 0.8941 0.8862 0.8783 0.8704 0.8625 0.8546 0.8467;           0.9398 0.9333 0.9268 0.9203 0.9138 0.9073 0.9008 0.8943;           0.9593 0.9544 0.9495 0.9446 0.9397 0.9348 0.9299 0.9250;             0.9549 0.9528 0.9507 0.9486 0.9465 0.9444 0.9423 0.9402;           0.9383 0.9391 0.9399 0.9407 0.9415 0.9423 0.9431 0.9439;             0.9103 0.9142 0.9181 0.9220 0.9259 0.9298 0.9337 0.9376;           0.8727 0.8798 0.8869 0.8940 0.9011 0.9082 0.9153 0.9224];% Add extrapolated portions to the mapMWS.LPT.NcVec    = [NcE MWS.LPT.NcVec];MWS.LPT.PRVec    = [PRE MWS.LPT.PRVec];MWS.LPT.WcArray  = [WcErw; [WcEcol MWS.LPT.WcArray]];MWS.LPT.effArray = [effErw; [effEcol MWS.LPT.effArray]];% extrapolate Nc for SS analysisNcMin = 10;NcMax = 125;for i = 1:length(MWS.LPT.PRVec)    WcArray_NcMin(i) = interp1(MWS.LPT.NcVec,MWS.LPT.WcArray(:,i)',NcMin,'linear','extrap');    effArray_NcMin(i) = interp1(MWS.LPT.NcVec,MWS.LPT.effArray(:,i)',NcMin,'linear','extrap');    WcArray_NcMax(i) = interp1(MWS.LPT.NcVec,MWS.LPT.WcArray(:,i)',NcMax,'linear','extrap');    effArray_NcMax(i) = interp1(MWS.LPT.NcVec,MWS.LPT.effArray(:,i)',NcMax,'linear','extrap');endMWS.LPT.NcVec = [NcMin MWS.LPT.NcVec NcMax];MWS.LPT.WcArray = [WcArray_NcMin; MWS.LPT.WcArray; WcArray_NcMax];MWS.LPT.effArray = [effArray_NcMin; MWS.LPT.effArray; effArray_NcMax];% extrapolate PR for SS analysisPRMin = 0.95;PRMax = 10;for i = 1:length(MWS.LPT.NcVec)    WcArray_PRMin(i) = interp1(MWS.LPT.PRVec,MWS.LPT.WcArray(i,:),PRMin,'linear','extrap');    effArray_PRMin(i) = interp1(MWS.LPT.PRVec,MWS.LPT.effArray(i,:),PRMin,'linear','extrap');    WcArray_PRMax(i) = interp1(MWS.LPT.PRVec,MWS.LPT.WcArray(i,:),PRMax,'linear','extrap');    effArray_PRMax(i) = interp1(MWS.LPT.PRVec,MWS.LPT.effArray(i,:),PRMax,'linear','extrap');endMWS.LPT.PRVec = [PRMin MWS.LPT.PRVec PRMax];MWS.LPT.WcArray = [WcArray_PRMin' MWS.LPT.WcArray WcArray_PRMax'];MWS.LPT.effArray = [effArray_PRMin' MWS.LPT.effArray effArray_PRMax'];